import React, { useState } from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

const Login = () => {
    const [inputField, setInputField] = useState({
        username: '',
        password: ''
    })

    const navigate = useNavigate()

    const handleChange = (e) => {
        setInputField({ ...inputField, [e.target.name]: e.target.value })
    }

    const handlelogin = async (e) => {
        e.preventDefault()
        const username = inputField.username
        const password = inputField.password
        try {
            const admin = await axios.post('https://login-api.web2rise.in/api/login', { username, password })
            const token = admin.data.token
            // console.log(token)
            if (token) {
                navigate('/Home')
            }
            localStorage.setItem('token', token)
        } catch (error) {
            console.log('error', error)
        }
    }

    return (
        <>
            <section class="login">
                <div class="login_box">
                    <div class="left">
                        <div class="top_link">
                        </div>
                        <div class="contact">
                            <form action="">
                                <h3>LOG IN PAGE</h3>
                                <input type="text" name='username' value={inputField.username} onChange={handleChange} placeholder="USERNAME" />
                                <input type="password" name='password' value={inputField.password} onChange={handleChange} placeholder="PASSWORD" />
                                <button class="submit" onClick={handlelogin}>LET'S GO</button>
                            </form>
                        </div>
                    </div>
                    <div class="right">
                        <div class="right-text">
                            <h2>WEB2RISE</h2>
                            <h5>A UX BASED CREATIVE AGENCEY</h5>
                        </div>
