import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const User = () => {
    const [allUsers, setAllUsers] = useState([]);

    const navigate = useNavigate()

    const getUsers = async () => {
        const token = localStorage.getItem('token');
        try {
            const response = await axios.get(
                "https://login-api.web2rise.in/api/users",
                {
                    headers: {
                        Authorization: token,
                        "Content-Type": "application/json",
                    },
                }
            );
            setAllUsers(response.data.data);
        } catch (error) {
            console.error(error);
        }
    };

    const deleteData = async (email) => {
        const token = localStorage.getItem("token");
        try {
            const resp = await axios.post(
                "https://login-api.web2rise.in/api/delete-user",
                { email: email },
                {
                    headers: {
                        Authorization: token,
                        "Content-Type": "application/json",
                    },
                }
            );
            if (resp.data.message === 'user deleted successfully') {
                getUsers();
            }
        } catch (error) {
            console.error(error);
        }
    };

    const handleEdit = (data) => {
        // console.log(data, "data")
        navigate('/edit', { state: { data } })
    }

    useEffect(() => {
        getUsers();
        // console.log(getUsers)
    }, []);

    return (
        <>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Sr.No</th>
                        <th scope="col">Name</th>
                        <th scope="col">Email</th>
                        <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                    {allUsers.map((user, index) => (
                        <tr key={user.id}>
                            <th scope="row">{index + 1}</th>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>
                                <button onClick={() => deleteData(user.email)}>Delete</button>
                                <button onClick={() => handleEdit(user)}>Edit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <div className="container py-4">
                <h1 className="text-center">USER DATA</h1>

                <form className="d-flex pb-5">
                    <input
                        className="form-control me-2"
                        type="search"
                        placeholder="Search"
                        aria-label="Search"
                    />
                </form>
                <table className="table border-1 table-bordered shadow text-center">
                    <thead className="table-dark">
                        <tr>
                            <th scope="col">SR.NO</th>
                            <th scope="col">PRODUCT NAME</th>
                            <th scope="col">PRICE</th>
                            <th scope="col">SALE (PRICE)</th>
                            <th scope="col">FRUITS IMAGE</th>
                            <th scope="col">ACTION</th>
                        </tr>
                    </thead>
                    <tbody className='align-middle'>
                        <tr>
                            <th scope="row">serialNumber</th>
                            <td>{user.fruitsname}</td>
                            <td>{user.price}</td>
                            <td>{user.saleprice}
                            </td>
                            <td>
                                <Link className="btn btn-warning mr">Edit</Link>
                                <button className="btn btn-danger mr">Delete</button>
                                <Link className="btn btn-primary">View All</Link>
                            </td>
                        </tr>
                    </tbody>
                </table>
                <nav aria-label="Page navigation example ">
                    <ul className="pagination justify-content-end">
                        <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`}>
                            <button
                                className="page-link settextcss"
                                onClick={() => paginate(currentPage - 1)}
                                disabled={currentPage === 1}
                            >
                                Previous
                            </button>
                        </li>
                        {Array.from({ length: Math.ceil(filteredUsers.length / usersPerPage) }).map((_, index) => (
                            <li className={`page-item ${currentPage === index + 1 ? 'active' : ''}`} key={index}>
                                <button className="page-link settextcss" onClick={() => paginate(index + 1)}>
                                    {index + 1}
                                </button>
                            </li>
                        ))}
                        <li className={`page-item ${currentPage === Math.ceil(filteredUsers.length / usersPerPage) ? 'disabled' : ''}`}>
                            <button
                                className="page-link settextcss"
                                onClick={() => paginate(currentPage + 1)}
                                disabled={currentPage === Math.ceil(filteredUsers.length / usersPerPage)}
                            >
                                Next
                            </button>
                        </li>
                    </ul>
                </nav>
            </div>
        </>
    );
}

export default User;
