import React, { useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom'

export const CreateUser = () => {

    const navigate = useNavigate()


    const [createuser, setCreateUser] = useState({
        name: "",
        email: "",
        password: ""
    })

    const userRegis = (e) => {
        setCreateUser({ ...createuser, [e.target.name]: e.target.value })
    }


    const registerUser = async (e) => {
        e.preventDefault()
        const name = createuser.name
        const email = createuser.email
        const password = createuser.password
        try {
            const admin = await axios.post('https://login-api.web2rise.in/api/login', { name, email, password })
            const token = admin.data.token
            console.log(token)
            if (token) {
                navigate('/User')
            }
            localStorage.setItem('token', token)
        } catch (error) {
            console.log('error', error)
        }
    }

    return (
        <>
            <div>
                <form action="">
                    <div class="input-box">
                        <label>Name :-</label>
                        <input type="text" name='name' value={createuser.name} onChange={userRegis} placeholder="Enter full name" required />
                    </div>
                    <div class="input-box">
                        <label>Email :-</label>
                        <input type="email" name='email' value={createuser.email} onChange={userRegis} placeholder="Enter full name" required />
                    </div>
                    <div class="input-box">
                        <label>Password :-</label>
                        <input type="password" name='password' value={createuser.password} onChange={userRegis} placeholder="Enter full name" required />
                    </div>
                    <button onClick={registerUser}>Registeration</button>
                    <NavLink to="/">
                        <button>Login</button>
                    </NavLink>
                </form>
            </div>
        </>
    )
}
