import React, { useState } from 'react';
import axios from 'axios';
import { NavLink, useNavigate } from 'react-router-dom';

export const CreateUser = () => {
    const navigate = useNavigate();

    const [createUser, setCreateUser] = useState({
        name: "",
        email: "",
        password: ""
    });

    const handleInputChange = (e) => {
        setCreateUser({ ...createUser, [e.target.name]: e.target.value });
    };

    const handleUserRegistration = async (e) => {
        e.preventDefault()
        const token = localStorage.getItem("token");
        const name = createUser.name;
        const email = createUser.email;
        const password = createUser.password;

        try {
            const resp = await axios.post(
                "https://login-api.web2rise.in/api/create-user", {
                name: name,
                email: email,
                password: password
            },
                {
                    headers: {
                        Authorization: token,
                        "Content-Type": "application/json",
                    },
                }
            );
            if (resp) {
                navigate('/User')
            }

            setCreateUser({
                name: "",
                email: "",
                password: ""
            })
            console.log(resp)

        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <form onSubmit={handleUserRegistration}>
                <div className="input-box">
                    <label>Name:</label>
                    <input type="text" name='name' value={createUser.name} onChange={handleInputChange} placeholder="Enter your name" required />
                </div>
                <div className="input-box">
                    <label>Email:</label>
                    <input type="email" name='email' value={createUser.email} onChange={handleInputChange} placeholder="Enter your email" required />
                </div>
                <div className="input-box">
                    <label>Password:</label>
                    <input type="password" name='password' value={createUser.password} onChange={handleInputChange} placeholder="Enter your password" required />
                </div>
                <button type="submit">Registration</button>
                <NavLink to="/">Login</NavLink>
            </form>
        </div>
    );
};
