import axios from "axios";
// import Cookies from 'js-cookie';
import React, { useState } from "react";
import { useLocation } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import register from "../images/register.jpg";

const Edit = () => {
  const location = useLocation();
  const AllData = location.state.data;
  console.log(AllData, "alldata");

  const [input, setInput] = useState({
    name: AllData.name,
    email: AllData.email,
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setInput({ ...input, [name]: value });
  };
  const handleUpdate = async (e) => {
    e.preventDefault();
    const { name, email } = input;
    // const token = localStorage.get('token');
    const token = localStorage.getItem("token");

    try {
      const updateResponse = await axios.post(
        "https://login-api.web2rise.in/api/update-user",
        { name, email },
        {
          headers: {
            Authorization: token,
            "Content-Type": "application/json",
          },
        }
      );

      console.log("User updated successfully:", updateResponse.data);

      if (updateResponse.data.message === "user updated successfully") {
        navigate("/User");
      }
    } catch (err) {
      console.log(err);
      alert("An error occurred while updating the user.");
    }
  };

  return (
    // <div className="container">
    //   <form>
    //     <h3>Update User</h3>
    //     <div className="form-group">
    //       <label htmlFor="exampleInputEmail1">Username</label>
    //       <input
    //         type="name"
    //         className="form-control"
    //         name="name"
    //         value={input.name}
    //         onChange={(e) => handleChange(e)}
    //         placeholder="Enter username"
    //       />
    //     </div>
    //     <div className="form-group">
    //       <label htmlFor="exampleInputPassword1">Email</label>
    //       <input
    //         type="text"
    //         className="form-control nav-link disabled"
    //         name="email"
    //         value={input.email}
    //         onChange={(e) => handleChange(e)}
    //         placeholder="email"
    //         aria-disabled="true"
    //       />
    //     </div>

    //     <button
    //       type="submit"
    //       className="btn btn-primary"
    //       onClick={handleUpdate}
    //     >
    //       Update
    //     </button>
    //   </form>
    // </div>

    <>
      <section className="signup">
        <div className="container mt-5 box_sdow">
          <div className="signup-contant">
            <div className="signup-form">
              <h2 className="form-tittle">Sign Up</h2>
              <form action="" className="register-form" id="register-form">
                <div className="form-group">
                  <label className="lebal_border" htmlFor="name">
                    <i class="zmdi zmdi-account"></i>
                  </label>
                  <input
                    name="name"
                    value={input.name}
                    onChange={(e) => handleChange(e)}
                    type="text"
                    autoComplete="off"
                    placeholder="Your Name"
                  />
                </div>

                <div className="form-group">
                  <label className="lebal_border" htmlFor="email">
                    <i class="zmdi zmdi-email"></i>
                  </label>
                  <input
                    name="email"
                    value={input.email}
                    onChange={(e) => handleChange(e)}
                    type="text"
                    autoComplete="off"
                    placeholder="Your Email"
                  />
                </div>

                <div className="form-froup form-button">
                  <button
                    type="button"
                    class="btn btn-success"
                    onClick={handleUpdate}
                  >
                    UPDATE
                  </button>
                </div>
              </form>
            </div>

            <div className="signup-image">
              <figure>
                <img
                  src={register}
                  className="img-fluid"
                  alt="registration pic"
                />
              </figure>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Edit;
