import axios from 'axios'
import Cookies from 'js-cookie';
import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const Edit = () => {
    const location = useLocation();
    const AllData = location.state.data;
    console.log(AllData, "alldata")

    const [input, setInput] = useState({
        name: AllData.name,
        email: AllData.email
    })

    const navigate = useNavigate()

    const handleChange = (e) => {
        const { name, value } = e.target;
        setInput({ ...input, [name]: value })

    }





    const handleUpdate = async (e) => {
        e.preventDefault()
        const { name, email } = input;
        const token = Cookies.get('token');

        try {
            const updateResponse = await axios.post(
                'https://login-api.web2rise.in/api/update-user',
                { name, email },
                {
                    headers: {
                        Authorization: token,
                        'Content-Type': 'application/json'
                    }
                }
            );

            console.log('User updated successfully:', updateResponse.data);

            if (updateResponse.data.message === 'user updated successfully') {
                navigate('/')
            }
        } catch (err) {
            console.log(err);
            alert('An error occurred while updating the user.');
        }
    };

    return (
        <div className='container'>
            <form>
                <h3>create User</h3>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Username</label>
                    <input type="name" className="form-control" name='name'
                        value={input.name} onChange={(e) => handleChange(e)} placeholder="Enter username" />
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Email</label>
                    <input type="text" className="form-control nav-link disabled" name='email'
                        value={input.email} onChange={(e) => handleChange(e)} placeholder="email" aria-disabled="true" />
                </div>


                <button type="submit" className="btn btn-primary" onClick={handleUpdate}>Submit</button>
            </form>
        </div>
    )
}

export default Edit
