import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { NavLink, useNavigate } from 'react-router-dom';

const User = () => {
    const [allUsers, setAllUsers] = useState([]);

    const navigate = useNavigate()

    const getUsers = async () => {
        const token = localStorage.getItem('token');
        try {
            const response = await axios.get(
                "https://login-api.web2rise.in/api/users",
                {
                    headers: {
                        Authorization: token,
                        "Content-Type": "application/json",
                    },
                }
            );
            setAllUsers(response.data.data);
        } catch (error) {
            console.error(error);
        }
    };

    const deleteData = async (email) => {
        const token = localStorage.getItem("token");
        try {
            const resp = await axios.post(
                "https://login-api.web2rise.in/api/delete-user",
                { email: email },
                {
                    headers: {
                        Authorization: token,
                        "Content-Type": "application/json",
                    },
                }
            );
            if (resp.data.message === 'user deleted successfully') {
                getUsers();
            }
        } catch (error) {
            console.error(error);
        }
    };

    const handleEdit = (data) => {
        // console.log(data, "data")
        navigate('/edit', { state: { data } })
    }

    useEffect(() => {
        getUsers();
        // console.log(getUsers)
    }, []);

    return (
        <>
            <div className="container py-4">
                <h1 className="text-center">USER DATA</h1>

                <form className="d-flex pb-5">
                    <input
                        className="form-control me-2"
                        type="search"
                        placeholder="Search"
                        aria-label="Search"
                    />
                </form>
                <table className="table border-1 table-bordered shadow text-center">
                    <thead className="table-dark">

                        <tr>
                            <th scope="col">SR.NO</th>
                            <th scope="col">NAME</th>
                            <th scope="col">G-MAIL</th>
                            <th scope="col">ACTION</th>
                        </tr>

                    </thead>
                    <tbody className='align-middle'>
                        {allUsers.map((user, index) => (
                            <tr key={user.id}>
                                <th scope="row">{index + 1}</th>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>
                                    <NavLink onClick={() => deleteData(user.email)} className="btn btn-primary">Delete</NavLink>
                                    <NavLink onClick={() => handleEdit(user)} className="btn btn-warning mr">Edit</NavLink>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
}

export default User;
