import React, { useState } from 'react'
import Axios from 'axios'
import { useNavigate } from 'react-router-dom'

const Login = () => {
    const [inputField, setInputField] = useState({
        username: '',
        password: ''
    })

    const navigate = useNavigate()

    const handleChange = (e) => {
        setInputField({ ...inputField, [e.target.name]: e.target.value })
    }

    const handlelogin = async (e) => {
        e.preventDefault()
        const username = inputField.username
        const password = inputField.password
        try {
            const admin = await axios.post('https://login-api.web2rise.in/api/login', { username, password })
            const token = admin.data.token
            console.log(token)
            if (token) {
                navigate('/Signup')
            }
            localStorage.setItem('token', token)
        } catch (error) {
            console.log('error', error)
        }
    }

    return (
        <>
            <div>
                <form action="">
                    <div class="input-box">
                        <label>Email :-</label>
                        <input type="text" name='username' value={inputField.username} onChange={handleChange} placeholder="Enter full name" required />
                    </div>
                    <div class="input-box">
                        <label>Password :-</label>
                        <input type="password" name='password' value={inputField.password} onChange={handleChange} placeholder="Enter full name" required />
                    </div>
                    <button onClick={handlelogin}>Login</button>
                </form>
            </div>
        </>
    )
}

export default Login
